#ifndef ASSETVIEWER_ICONS_H
#define ASSETVIEWER_ICONS_H

#include "TGUI/Texture.hpp"

namespace rumpedav {

    // Icons: https://pictogrammers.com/library/mdi/
    // Base64: https://base64.guru/converter/encode/image

#define ICON_CROSS_BLACK ""

    class Icons {

    public:
        static tgui::Texture cross() {
            tgui::Texture icon;
            icon.loadFromBase64(
                    tgui::CharStringView(ICON_CROSS_BLACK),
                    {0, 0, 256, 256},
                    {0, 0, 256, 256},
                    true
            );
            return icon;
        }
    };

}

#endif //ASSETVIEWER_ICONS_H
